import { Form, Icon, Input, Button } from 'antd'
import React from 'react'
import { Redirect } from 'react-router-dom'
import connect from '../../utils/connect'
import { login } from './service'
import './index.less'
@connect
class NormalLoginForm extends React.Component {
    handleSubmit = e => {
        const _this = this
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
            if (!err) {
                _this.authChange(values)
            }else{
                console.log(err)
            }
        })
    }

    authChange =  (values)=>{
        const { dispatch, authChangeAction } = this.props
        login(values).then(res =>{
            const action =  authChangeAction(res.data.token)
            dispatch(action)
        })
    }

    render() {
        if(this.props.state.authed ||localStorage.getItem('authed')){
            return (
                <Redirect to="/user" />
            )
        }
        const { getFieldDecorator } = this.props.form;
        return (
            <div className="flex_center wrapper_login">
                <Form onSubmit={this.handleSubmit} className="login-form login-form-login">
                    <div className="login-title">后台管理系统</div>
                    <Form.Item>
                        {getFieldDecorator('username', {
                            rules: [{ required: true, message: 'Please input your username!' }],
                        })(
                            <Input
                                prefix={<Icon type="user" />}
                                placeholder="username"
                            />,
                        )}
                    </Form.Item>
                    <Form.Item>
                        {getFieldDecorator('password', {
                            rules: [{ required: true, message: 'Please input your Password!' }],
                        })(
                            <Input
                                prefix={<Icon type="lock" />}
                                type="password"
                                placeholder="password"
                            />,
                        )}
                    </Form.Item>
                    <Form.Item>
                        <Button type="primary" htmlType="submit" className="login-form-button">
                            Log in
                        </Button>
                    </Form.Item>
                    <Form.Item>
                        <div className="loginTip">用户为admin的时候，能够看到所有的权限列表</div>
                    </Form.Item>
                </Form>
            </div>
        );
    }
}

const WrappedNormalLoginForm = Form.create({ name: 'normal_login' })(NormalLoginForm)
export default WrappedNormalLoginForm
